func:
var temp = Number(msg.payload)
var myData = flow.get("myData")
var currentth1 = Number(myData.th1);
var currentth2 = Number(myData.th2);
var currentth3 = Number(myData.th3);
var currentth4 = Number(myData.th4);


if (temp < currentth1) {
    msg.payload = "Too Cold";
}
else if ((currentth1 < temp) && (temp <= currentth2)) {
    msg.payload = "Warning Cold";
}
else if ((currentth2 < temp) && (temp <= currentth3)) {
    msg.payload = "Good Temp";
}
else if ((currentth3 < temp) && (temp <= currentth4)) {
    msg.payload = "Warning Hot";
}
else {
    msg.payload = "Too Hot";
}


return msg;
